               0,           0,                0,                0,             -Od1, dc - dr,                  0,              Oc1,              Op1,               0,                0,    -g24,                  0,                0,               0;...
               0,           0,                0,                0,                0,    -Op1,                Od2, -Oa*cos(theta_m),     dp - dc + dr,               0,                0,       0,                  0,  Oa*sin(theta_m),            -gmr;...
             Od2,         Od2,            2*Od2,                0,                0,    -Oc1,                  0,               dr, -Oa*cos(theta_m),               0,                0,       0,                  0,              g43, Oa*sin(theta_m)];
 
    B=[0; 0; 0; 0; 0; 0; 0; 0;-G43; 0; 0; 0; 0; 0; Od2];
    x=A\B;

